installation.script:
#!/bin/bash
set -euo pipefail
echo "[install] start"; [ -z "${REPO_URL:-}" ] && echo "REPO_URL required" && exit 1; [ -z "${REG_TOKEN:-}" ] && echo "REG_TOKEN required" && exit 1; RV=2.329.0; SH=194f1e1e4bd02f80b7e9633fc546084d8d4e19f3928a324d512ea53430102e1d; mkdir -p /mnt/server/actions-runner; cd /mnt/server/actions-runner; echo "[install] downloading"; curl -fsSL -o runner.tar.gz -L https://github.com/actions/runner/releases/download/v${RV}/actions-runner-linux-x64-${RV}.tar.gz; echo "[install] verifying"; echo "${SH}  runner.tar.gz" | sha256sum -c -; echo "[install] extracting"; tar xzf runner.tar.gz; echo "[install] chown"; chown -R container:container /mnt/server/actions-runner; echo "[install] done"
